import styled, { css } from 'styled-components';
import { shade } from 'polished';

interface FormProps {
  hasError: boolean
}

const Title = styled.h1`
  font-size: 48px;
  color: #3a3a3a;
  max-width: 450px;
  line-height: 56px;
  margin-top: 80px;
`;

const Form = styled.form<FormProps>`
  margin-top: 40px;
  max-width: 700px;
  display: flex;
  input {
    flex: 1;
    height: 70px;
    padding: 0 20px;
    border: 0;
    border-radius: 5px 0 0 5px;
    color: #3a3a3a;
    border: 2px solid #FFF;
    border-right: 0;
    ${(props) => props.hasError && css`
      border-color: #c53030;
    `}
    &::placeholder {
      color: #a8a8b3
    }
  }
  button {
    width: 210px;
    height: 70px;
    background-color: #04d461;
    border: 0;
    border-radius: 0 5px 5px 0;
    color: #FFF;
    font-weight: bold;
    transition: background-color 0.2s;
    &:hover {
      background: ${shade(0.1, '#04d461')}
    }
  }
`;
const Repositories = styled.div`
  margin-top: 80px;
  max-width: 700px;
  a {
    background: #FFF;
    border-radius: 5px;
    width: 100%;
    padding: 24px;
    display: block;
    text-decoration: none;
    display: flex;
    align-items: center;
    transition: transform 0.2s;
    & + a {
      margin-top: 16px;
    }
    &:hover  {
      transform: translateX(10px)
    }
  }
  img {
    width: 64px;
    height: 64px;
    border-radius: 50%
  }
  div {
    margin: 0 16px;
    flex: 1;
    strong {
      font-size: 20px;
      color: #3d3d4d
    }
    p {
      font-size: 18px;
      color: #a8a8b3;
      margin-top: 4px;
    }
  }
  svg {
    margin-left: auto;
    color: #cbcbd6
  }
`;

const Error = styled.span`
  display: block;
  color: #c43030;
  margin-top: 30px
`;

export {
  Title, Form, Repositories, Error,
};
